    const url = "https://agent.api.lyzr.app/v3/providers/credentials/user/{provider_type}/{provider_id}";
    const headers = {
        'accept': 'application/json'
    };

    axios.get(url, { headers })
        .then(response => {
            if (response.status === 422) {
                console.log(response.data);
            } else {
                console.error(`Error: ${response.status} - ${response.statusText}`);
            }
        })
        .catch(error => {
            console.error(`Error: ${error.response.status} - ${error.response.statusText}`);
        });

java.value:
import java.io.IOException;
    import java.io.InputStreamReader;
    import java.io.BufferedReader;
    import java.net.HttpURLConnection;
    import java.net.URL;

    public class Main {
        public static void main(String[] args) throws IOException {
            URL url = new URL("https://agent.api.lyzr.app/v3/providers/credentials/user/{provider_type}/{provider_id}");
            HttpURLConnection conn = (HttpURLConnection) url.openConnection();
            conn.setRequestMethod("GET");
            conn.setRequestProperty("accept", "application/json");

            int responseCode = conn.getResponseCode();
            if (responseCode == 422) {
                BufferedReader in = new BufferedReader(new InputStreamReader(conn.getInputStream()));
                String inputLine;
                StringBuilder content = new StringBuilder();
                while ((inputLine = in.readLine()) != null) {
                    content.append(inputLine);
                }
                in.close();
                System.out.println(content.toString());
            } else {
                System.out.println("Error: " + responseCode + " - " + conn.getResponseMessage());
            }
        }
    }

